'    p_plugin in apex_plugin.t_plugin',
') return apex_plugin.t_region_ajax_result is',
'    -- Map region attributes to function constants',
'    -- MODERN, CLASSIC',
'    c_chart_type                 constant varchar2(7)     := p_region.attribute_15;',
